mapper.select:
SELECT *
FROM dailywrite
WHERE user_code = #{user_code} AND date = #{date}

SELECT *
FROM dailywrite
WHERE user_code = #{user_code} AND date BETWEEN #{start} AND #{end}

mapper.update:
UPDATE dailywrite
SET home = #{home},
    school = #{school},
    hospital = #{hospital},
    summary = #{summary}
WHERE user_code = #{user_code} AND date = #{date}

mapper.delete:
DELETE
FROM dailywrite
WHERE user_code = #{user_code} AND date = #{date}
